    transition: all 0.4s cubic-bezier(0.23, 1, 0.32, 1) !important;
  }

  /* Primary color transitions */
  .bg-primary, [data-state="active"]:not([data-state="inactive"]) {
    transition: background-color 0.4s cubic-bezier(0.23, 1, 0.32, 1) !important;
  }

  .physics-theme {
    background-color: hsl(213.91 100% 96%);
    border-color: hsl(213.91 100% 85%);
  }

  .chemistry-theme {
    background-color: hsl(159.78 100% 96%);
    border-color: hsl(159.78 100% 85%);
  }

  .mathematics-theme {
    background-color: hsl(258.18 73.91% 96%);
    border-color: hsl(258.18 73.91% 85%);
  }
}

@layer utilities {
  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  .animate-pulse-soft {
    animation: pulse 2s ease-in-out infinite;
  }

  .animate-slide-up {
    animation: slideUp 0.3s ease-out;
  }

  .animate-fade-in {
    animation: fadeIn 0.6s ease-out forwards;
    opacity: 0;
  }

  .animate-scale-in {
    animation: scaleIn 0.4s ease-out forwards;
    opacity: 0;
    transform: scale(0.95);
  }

  .animate-slide-in-left {
    animation: slideInLeft 0.5s ease-out forwards;
    opacity: 0;
    transform: translateX(-20px);
  }

  .animate-slide-in-right {
    animation: slideInRight 0.5s ease-out forwards;
    opacity: 0;
    transform: translateX(20px);
  }

  .animate-bounce-subtle {
    animation: bounceSubtle 2s ease-in-out infinite;
  }

  .animate-glow {
    animation: glow 2s ease-in-out infinite alternate;
  }

  .line-clamp-2 {
    overflow: hidden;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 2;
  }

  .line-clamp-3 {
    overflow: hidden;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 3;
  }
}

