    transform: translateY(0px);
  }
  50% { 
    transform: translateY(-10px);
  }
}

@keyframes slideUp {
  0% { 
    transform: translateY(10px);
    opacity: 0;
  }
  100% { 
    transform: translateY(0px);
    opacity: 1;
  }
}

@keyframes fadeIn {
  0% { 
    opacity: 0;
  }
  100% { 
