client/src/pages/resources.tsx.content:
import { useState, useEffect } from 'react';
import { Plus, Search, ExternalLink, Book, Video, Calculator, FileText, Trash2, Edit, Filter } from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { SimpleResourceModal } from '@/components/new-modals/simple-resource-modal';
import { SimpleConfirmationDialog } from '@/components/new-modals/simple-confirmation-dialog';
import { resourceStorage } from '@/lib/storage';
import { useToast } from '@/hooks/use-toast';
import type { Resource, Subject } from '@shared/schema';

const categoryIcons = {
  book: Book,
  video: Video,
  tool: Calculator,
  pdf: FileText,
  website: ExternalLink,
};

const categoryColors = {
  book: 'bg-blue-100 text-blue-600 border-blue-200',
  video: 'bg-green-100 text-green-600 border-green-200',
  tool: 'bg-purple-100 text-purple-600 border-purple-200',
  pdf: 'bg-red-100 text-red-600 border-red-200',
  website: 'bg-gray-100 text-gray-600 border-gray-200',
};

const subjectColors = {
  Physics: 'bg-blue-100 text-blue-800 border-blue-200',
  Chemistry: 'bg-green-100 text-green-800 border-green-200',
  Mathematics: 'bg-purple-100 text-purple-800 border-purple-200',
  General: 'bg-gray-100 text-gray-800 border-gray-200',
};

export default function Resources() {
  const [resources, setResources] = useState<Resource[]>([]);
  const [filteredResources, setFilteredResources] = useState<Resource[]>([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedSubject, setSelectedSubject] = useState<Subject | 'General' | 'All'>('All');
  const [selectedCategory, setSelectedCategory] = useState<string>('All');
  const [isResourceModalOpen, setIsResourceModalOpen] = useState(false);
  const [deleteConfirmation, setDeleteConfirmation] = useState<{ open: boolean; resourceId?: string; resourceTitle?: string }>({
    open: false,
  });
  const { toast } = useToast();

  useEffect(() => {
    loadResources();
  }, []);

  useEffect(() => {
    filterResources();
  }, [resources, searchQuery, selectedSubject, selectedCategory]);

  const loadResources = () => {
    const allResources = resourceStorage.getAll();
    setResources(allResources);
  };

  const handleResourceCreated = () => {
    loadResources();
  };

  const handleDeleteResource = (id: string, title: string) => {
    setDeleteConfirmation({
      open: true,
      resourceId: id,
      resourceTitle: title,
    });
  };

  const confirmDeleteResource = () => {
    if (deleteConfirmation.resourceId) {
      const success = resourceStorage.delete(deleteConfirmation.resourceId);
      if (success) {
        toast({
          title: 'Resource Deleted',
          description: `Resource "${deleteConfirmation.resourceTitle}" has been deleted successfully.`,
        });
        loadResources();
      } else {
        toast({
          title: 'Error',
          description: 'Failed to delete resource. Please try again.',
          variant: 'destructive',
        });
      }
    }
  };

  const filterResources = () => {
    let filtered = resources;

    // Filter by search query
    if (searchQuery) {
      filtered = filtered.filter(resource =>
        resource.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        resource.description?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        resource.url.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Filter by subject
    if (selectedSubject !== 'All') {
      filtered = filtered.filter(resource => resource.subject === selectedSubject);
    }

    // Filter by category
    if (selectedCategory !== 'All') {
      filtered = filtered.filter(resource => resource.category === selectedCategory);
    }

    setFilteredResources(filtered);
  };

  const handleResourceClick = (url: string) => {
    window.open(url, '_blank', 'noopener,noreferrer');
  };

  const getResourceStats = () => {
    const bySubject = resources.reduce((acc, resource) => {
      acc[resource.subject] = (acc[resource.subject] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    const byCategory = resources.reduce((acc, resource) => {
      acc[resource.category] = (acc[resource.category] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    return { bySubject, byCategory, total: resources.length };
  };

  const stats = getResourceStats();

  return (
    <div className="space-y-6 animate-fade-in">
      {/* Header */}
      <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between">
        <div>
          <h1 className="text-3xl font-bold text-foreground mb-2">Resources</h1>
          <p className="text-muted-foreground">Organize and access your study materials and helpful links</p>
        </div>
        <Button
          onClick={() => setIsResourceModalOpen(true)}
          className="mt-4 lg:mt-0 bg-primary text-primary-foreground px-6 py-3 rounded-xl font-medium hover:bg-primary/90 transition-all duration-200 shadow-lg hover:shadow-xl transform hover:-translate-y-1"
          data-testid="button-add-resource"
        >
          <Plus className="mr-2" size={18} />
          Add New Resource
        </Button>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card className="p-4 bg-card border-border">
          <CardContent className="p-0">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Total Resources</p>
                <p className="text-2xl font-bold text-foreground">{stats.total}</p>
              </div>
              <div className="w-10 h-10 bg-blue-50 dark:bg-blue-900/20 rounded-lg flex items-center justify-center">
                <ExternalLink className="text-blue-600 dark:text-blue-400" size={20} />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="p-4 bg-card border-border">
          <CardContent className="p-0">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Videos</p>
                <p className="text-2xl font-bold text-green-600 dark:text-green-400">{stats.byCategory.video || 0}</p>
              </div>
              <div className="w-10 h-10 bg-green-50 dark:bg-green-900/20 rounded-lg flex items-center justify-center">
                <Video className="text-green-600 dark:text-green-400" size={20} />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="p-4 bg-card border-border">
          <CardContent className="p-0">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Books</p>
                <p className="text-2xl font-bold text-purple-600 dark:text-purple-400">{stats.byCategory.book || 0}</p>
              </div>
              <div className="w-10 h-10 bg-purple-50 dark:bg-purple-900/20 rounded-lg flex items-center justify-center">
                <Book className="text-purple-600 dark:text-purple-400" size={20} />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="p-4 bg-card border-border">
          <CardContent className="p-0">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Websites</p>
                <p className="text-2xl font-bold text-muted-foreground">{stats.byCategory.website || 0}</p>
              </div>
              <div className="w-10 h-10 bg-muted rounded-lg flex items-center justify-center">
                <ExternalLink className="text-muted-foreground" size={20} />
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <Card className="p-6">
        <div className="flex flex-col lg:flex-row gap-4">
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={18} />
              <Input
                placeholder="Search resources..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
                data-testid="input-search-resources"
              />
            </div>
          </div>

          <div className="flex gap-2">
            <select
              value={selectedSubject}
              onChange={(e) => setSelectedSubject(e.target.value as Subject | 'General' | 'All')}
              className="px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-jee-primary"
              data-testid="select-filter-subject"
            >
              <option value="All">All Subjects</option>
              <option value="Physics">Physics</option>
              <option value="Chemistry">Chemistry</option>
              <option value="Mathematics">Mathematics</option>
              <option value="General">General</option>
            </select>

            <select
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
              className="px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-jee-primary"
              data-testid="select-filter-category"
            >
              <option value="All">All Categories</option>
              <option value="video">Videos</option>
              <option value="book">Books</option>
              <option value="pdf">PDFs</option>
              <option value="website">Websites</option>
              <option value="tool">Tools</option>
            </select>
          </div>
        </div>
      </Card>

      {/* Resources Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredResources.length === 0 ? (
          <div className="col-span-full">
            <Card className="p-12">
              <div className="text-center">
                <ExternalLink className="mx-auto mb-4 text-gray-400" size={48} />
                <h3 className="text-lg font-medium text-gray-900 mb-2">No resources found</h3>
                <p className="text-jee-muted mb-4">
                  {resources.length === 0 
                    ? "Add your first resource to build your study library!"
                    : "Try adjusting your search or filter criteria."
                  }
                </p>
                {resources.length === 0 && (
                  <Button
                    onClick={() => setIsResourceModalOpen(true)}
                    className="bg-primary text-primary-foreground hover:bg-primary/90"
                  >
                    <Plus className="mr-2" size={16} />
                    Add Your First Resource
                  </Button>
                )}
              </div>
            </Card>
          </div>
        ) : (
          filteredResources.map((resource) => {
            const IconComponent = categoryIcons[resource.category];
            const categoryColor = categoryColors[resource.category];
            const subjectColor = subjectColors[resource.subject];

            return (
              <Card
                key={resource.id}
                className="group hover:shadow-lg transition-all duration-200 cursor-pointer animate-scale-in"
                data-testid={`resource-card-${resource.id}`}
              >
                <CardContent className="p-6">
                  <div className="flex items-start justify-between mb-4">
                    <div className={`w-12 h-12 rounded-xl flex items-center justify-center ${categoryColor}`}>
                      <IconComponent size={20} />
                    </div>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteResource(resource.id, resource.title);
                      }}
                      className="opacity-0 group-hover:opacity-100 transition-opacity text-red-500 hover:text-red-700"
                      data-testid={`button-delete-${resource.id}`}
                    >
                      <Trash2 size={16} />
                    </Button>
                  </div>

                  <div
                    onClick={() => handleResourceClick(resource.url)}
                    className="cursor-pointer"
                  >
                    <h3 className="font-semibold text-foreground mb-2 group-hover:text-primary transition-colors">
                      {resource.title}
                    </h3>

                    {resource.description && (
                      <p className="text-sm text-muted-foreground mb-4 line-clamp-2">
                        {resource.description}
                      </p>
                    )}

                    <div className="flex items-center justify-between">
                      <div className="flex gap-2">
                        <Badge className={subjectColor}>
                          {resource.subject}
                        </Badge>
                        <Badge className={categoryColor}>
                          {resource.category}
                        </Badge>
                      </div>
                      <ExternalLink 
                        size={16} 
                        className="text-muted-foreground group-hover:text-primary transition-colors" 
                      />
                    </div>
                  </div>
                </CardContent>
              </Card>
            );
          })
        )}
      </div>

      {/* Resource Modal */}
      <SimpleResourceModal
        open={isResourceModalOpen}
        onClose={() => setIsResourceModalOpen(false)}
        onResourceCreated={handleResourceCreated}
      />

      {/* Confirmation Dialog */}
      <SimpleConfirmationDialog
        open={deleteConfirmation.open}
        onClose={() => setDeleteConfirmation({ ...deleteConfirmation, open: false })}
        onConfirm={confirmDeleteResource}
        title="Delete Resource"
        description={`Are you sure you want to delete "${deleteConfirmation.resourceTitle}"? This action cannot be undone.`}
        confirmText="Delete"
        cancelText="Cancel"
        variant="destructive"
        icon="delete"
      />
    </div>
  );
}

client/src/pages/subjects.tsx.content:
import { useState, useEffect } from 'react';
import { Atom, FlaskConical, Calculator, Plus, TrendingUp, Clock, CheckCircle } from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { SimpleTaskModal } from '@/components/new-modals/simple-task-modal';
import { taskStorage, studySessionStorage, userStatsStorage } from '@/lib/storage';
import type { Subject, Task } from '@shared/schema';

const subjectConfig = {
  Physics: {
    icon: Atom,
    color: 'blue',
    bgGradient: 'from-blue-500 to-blue-600',
    lightBg: 'bg-blue-50',
    description: 'Master the fundamental laws of nature and their applications',
    topics: ['Mechanics', 'Thermodynamics', 'Electromagnetism', 'Optics', 'Modern Physics'],
  },
  Chemistry: {
    icon: FlaskConical,
    color: 'green',
    bgGradient: 'from-green-500 to-green-600',
    lightBg: 'bg-green-50',
    description: 'Explore the composition, structure, and properties of matter',
    topics: ['Organic Chemistry', 'Inorganic Chemistry', 'Physical Chemistry', 'Environmental Chemistry'],
  },
  Mathematics: {
    icon: Calculator,
    color: 'purple',
    bgGradient: 'from-purple-500 to-purple-600',
    lightBg: 'bg-purple-50',
    description: 'Develop logical thinking and problem-solving skills',
    topics: ['Algebra', 'Calculus', 'Geometry', 'Trigonometry', 'Statistics & Probability'],
  },
};

export default function Subjects() {
  const [selectedSubject, setSelectedSubject] = useState<Subject>('Physics');
  const [isTaskModalOpen, setIsTaskModalOpen] = useState(false);
  const [tasks, setTasks] = useState<Task[]>([]);

  useEffect(() => {
    loadTasks();
  }, []);

  const loadTasks = () => {
    setTasks(taskStorage.getAll());
  };

  const getSubjectStats = (subject: Subject) => {
    const subjectTasks = tasks.filter(task => task.subject === subject);
    const completedTasks = subjectTasks.filter(task => task.status === 'completed');
    const sessions = studySessionStorage.getBySubject(subject);
    const totalStudyTime = sessions.reduce((acc, session) => acc + session.duration, 0);

    const progress = subjectTasks.length > 0 ? (completedTasks.length / subjectTasks.length) * 100 : 0;

    return {
      totalTasks: subjectTasks.length,
      completedTasks: completedTasks.length,
      progress: Math.round(progress),
      totalStudyTime: Math.round(totalStudyTime / 60 * 10) / 10, // Convert to hours
      recentTasks: subjectTasks.slice(0, 5),
    };
  };

  const handleTaskCreated = () => {
    loadTasks();
  };

  const overallStats = userStatsStorage.get();

  return (
    <div className="space-y-6 animate-fade-in">
      {/* Header */}
      <div>
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Subjects</h1>
        <p className="text-jee-muted">Track your progress across Physics, Chemistry, and Mathematics</p>
      </div>

      {/* Subject Overview Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {(Object.keys(subjectConfig) as Subject[]).map((subject) => {
          const config = subjectConfig[subject];
          const stats = getSubjectStats(subject);
          const IconComponent = config.icon;

          return (
            <Card
              key={subject}
              className={`cursor-pointer transition-all duration-300 hover:shadow-lg transform hover:-translate-y-1 ${
                selectedSubject === subject ? 'ring-2 ring-jee-primary' : ''
              }`}
              onClick={() => setSelectedSubject(subject)}
              data-testid={`subject-card-${subject.toLowerCase()}`}
            >
              <CardContent className="p-6">
                <div className={`w-full h-32 bg-gradient-to-br ${config.bgGradient} rounded-xl mb-4 flex items-center justify-center`}>
                  <IconComponent className="text-white" size={48} />
                </div>

                <h3 className="text-xl font-bold text-gray-900 mb-2">{subject}</h3>
                <p className="text-sm text-jee-muted mb-4">{config.description}</p>

                <div className="space-y-3">
                  <div className="flex items-center justify-between text-sm">
                    <span className="text-jee-muted">Progress</span>
                    <span className="font-semibold text-gray-900">{stats.progress}%</span>
                  </div>
                  <Progress value={stats.progress} className="h-2" />

                  <div className="grid grid-cols-2 gap-4 pt-2">
                    <div className="text-center">
                      <p className="text-lg font-bold text-gray-900">{stats.completedTasks}</p>
                      <p className="text-xs text-jee-muted">Completed</p>
                    </div>
                    <div className="text-center">
                      <p className="text-lg font-bold text-gray-900">{stats.totalStudyTime}h</p>
                      <p className="text-xs text-jee-muted">Study Time</p>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* Selected Subject Details */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Subject Details */}
        <div className="lg:col-span-2 space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center space-x-3">
                {(() => {
                  const IconComponent = subjectConfig[selectedSubject].icon;
                  return <IconComponent className={`text-${subjectConfig[selectedSubject].color}-600`} size={24} />;
                })()}
                <span>{selectedSubject} Details</span>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-6">
                {/* Topics */}
                <div>
                  <h4 className="font-semibold text-gray-900 mb-3">Key Topics</h4>
                  <div className="flex flex-wrap gap-2">
                    {subjectConfig[selectedSubject].topics.map((topic) => (
                      <Badge
                        key={topic}
                        variant="outline"
                        className={`${subjectConfig[selectedSubject].lightBg} border-${subjectConfig[selectedSubject].color}-200`}
                      >
                        {topic}
                      </Badge>
                    ))}
                  </div>
                </div>

                {/* Recent Tasks */}
                <div>
                  <div className="flex items-center justify-between mb-3">
                    <h4 className="font-semibold text-gray-900">Recent Tasks</h4>
                    <Button
                      onClick={() => setIsTaskModalOpen(true)}
                      size="sm"
                      variant="outline"
                      className="text-jee-primary hover:text-blue-700"
                      data-testid="button-add-subject-task"
                    >
                      <Plus size={16} className="mr-1" />
                      Add Task
                    </Button>
                  </div>

                  <div className="space-y-3">
                    {(() => {
                      const stats = getSubjectStats(selectedSubject);
                      return stats.recentTasks.length === 0 ? (
                        <div className="text-center py-8 text-jee-muted">
                          <CheckCircle className="mx-auto mb-3 opacity-50" size={24} />
                          <p className="text-sm">No tasks for {selectedSubject} yet</p>
                          <p className="text-xs">Create your first task to get started!</p>
                        </div>
                      ) : (
                        stats.recentTasks.map((task) => (
                          <div
                            key={task.id}
                            className={`p-3 rounded-lg border transition-all hover:shadow-sm ${subjectConfig[selectedSubject].lightBg}`}
                            data-testid={`subject-task-${task.id}`}
                          >
                            <div className="flex items-center justify-between">
                              <div>
                                <p className={`font-medium ${task.status === 'completed' ? 'line-through text-gray-500' : 'text-gray-900'}`}>
                                  {task.title}
                                </p>
                                <p className="text-xs text-jee-muted">
                                  Due: {new Date(task.dueDate).toLocaleDateString()}
                                </p>
                              </div>
                              <Badge
                                className={
                                  task.status === 'completed'
                                    ? 'bg-green-100 text-green-800'
                                    : task.status === 'in-progress'
                                    ? 'bg-yellow-100 text-yellow-800'
                                    : 'bg-gray-100 text-gray-800'
                                }
                              >
                                {task.status}
                              </Badge>
                            </div>
                          </div>
                        ))
                      );
                    })()}
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Subject Stats Sidebar */}
        <div className="space-y-6">
          {/* Progress Stats */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Progress Statistics</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              {(() => {
                const stats = getSubjectStats(selectedSubject);
                return (
                  <>
                    <div className="flex items-center justify-between">
                      <span className="text-sm text-jee-muted">Total Tasks</span>
                      <span className="font-semibold">{stats.totalTasks}</span>
                    </div>
                    <div className="flex items-center justify-between">
                      <span className="text-sm text-jee-muted">Completed</span>
                      <span className="font-semibold text-green-600">{stats.completedTasks}</span>
                    </div>
                    <div className="flex items-center justify-between">
                      <span className="text-sm text-jee-muted">Study Hours</span>
                      <span className="font-semibold text-blue-600">{stats.totalStudyTime}h</span>
                    </div>
                    <div className="flex items-center justify-between">
                      <span className="text-sm text-jee-muted">Completion Rate</span>
                      <span className="font-semibold">{stats.progress}%</span>
                    </div>
                  </>
                );
              })()}
            </CardContent>
          </Card>

          {/* Overall Progress Comparison */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Subject Comparison</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              {(Object.keys(subjectConfig) as Subject[]).map((subject) => {
                const stats = getSubjectStats(subject);
                const config = subjectConfig[subject];

                return (
                  <div key={subject} className="space-y-2">
                    <div className="flex items-center justify-between">
                      <span className="text-sm font-medium">{subject}</span>
                      <span className="text-sm text-jee-muted">{stats.progress}%</span>
                    </div>
                    <Progress 
                      value={stats.progress} 
                      className={`h-2 [&>div]:bg-${config.color}-600`}
                    />
                  </div>
                );
              })}
            </CardContent>
          </Card>

          {/* Study Streak */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg flex items-center">
                <TrendingUp className="mr-2" size={18} />
                Study Streak
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-center">
                <div className="text-3xl font-bold text-jee-primary mb-2">
                  {overallStats.currentStreak}
                </div>
                <p className="text-sm text-jee-muted">Days in a row</p>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>

      {/* Task Modal */}
      <SimpleTaskModal
        open={isTaskModalOpen}
        onClose={() => setIsTaskModalOpen(false)}
        onTaskCreated={handleTaskCreated}
      />
    </div>
  );
}

