import { useState, useEffect } from 'react';
import { Plus, Search, Filter, MoreHorizontal, CheckCircle, Clock, AlertCircle, Trash2 } from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Checkbox } from '@/components/ui/checkbox';
import { SimpleTaskModal } from '@/components/new-modals/simple-task-modal';
import { taskStorage } from '@/lib/storage';
import type { Task, Subject } from '@shared/schema';

const subjectColors = {
  Physics: 'bg-blue-100 text-blue-800 border-blue-200',
  Chemistry: 'bg-green-100 text-green-800 border-green-200',
  Mathematics: 'bg-purple-100 text-purple-800 border-purple-200',
};

const statusConfig = {
  completed: { 
    label: 'Completed', 
    icon: CheckCircle, 
    className: 'bg-green-100 text-green-800 border-green-200' 
  },
  'in-progress': { 
    label: 'In Progress', 
    icon: Clock, 
    className: 'bg-yellow-100 text-yellow-800 border-yellow-200' 
  },
  pending: { 
    label: 'Pending', 
    icon: Clock, 
    className: 'bg-gray-100 text-gray-800 border-gray-200' 
  },
  overdue: { 
    label: 'Overdue', 
    icon: AlertCircle, 
    className: 'bg-red-100 text-red-800 border-red-200' 
  },
};

export default function Tasks() {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [filteredTasks, setFilteredTasks] = useState<Task[]>([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedSubject, setSelectedSubject] = useState<Subject | 'All'>('All');
  const [selectedStatus, setSelectedStatus] = useState<string>('All');
  const [isTaskModalOpen, setIsTaskModalOpen] = useState(false);

  useEffect(() => {
    loadTasks();
  }, []);

  useEffect(() => {
    filterTasks();
  }, [tasks, searchQuery, selectedSubject, selectedStatus]);

  const loadTasks = () => {
    const allTasks = taskStorage.getAll();
    setTasks(allTasks);
  };

  const filterTasks = () => {
    let filtered = tasks;

    // Filter by search query
    if (searchQuery) {
      filtered = filtered.filter(task =>
        task.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        task.description?.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Filter by subject
    if (selectedSubject !== 'All') {
      filtered = filtered.filter(task => task.subject === selectedSubject);
    }

    // Filter by status
    if (selectedStatus !== 'All') {
      filtered = filtered.filter(task => task.status === selectedStatus);
    }

    setFilteredTasks(filtered);
  };

  const handleTaskToggle = (taskId: string, completed: boolean) => {
    const updates = { status: completed ? 'completed' as const : 'pending' as const };
    const updatedTask = taskStorage.update(taskId, updates);

    if (updatedTask) {
      loadTasks();
    }
  };

  const handleTaskCreated = () => {
    loadTasks();
  };

  const handleDeleteTask = (taskId: string) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      const success = taskStorage.delete(taskId);
      if (success) {
        loadTasks();
      }
    }
  };

  const getTaskStatus = (task: Task): keyof typeof statusConfig => {
    if (task.status === 'completed') return 'completed';
    if (task.status === 'in-progress') return 'in-progress';

    const now = new Date();
    const dueDate = new Date(task.dueDate);

    if (dueDate < now) return 'overdue';

    return 'pending';
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: date.getFullYear() !== new Date().getFullYear() ? 'numeric' : undefined,
    });
  };

  const taskStats = {
    total: tasks.length,
    completed: tasks.filter(task => task.status === 'completed').length,
    pending: tasks.filter(task => task.status !== 'completed').length,
    overdue: tasks.filter(task => {
      const now = new Date();
      const dueDate = new Date(task.dueDate);
      return dueDate < now && task.status !== 'completed';
    }).length,
  };

  return (
    <div className="space-y-6 animate-fade-in">
      {/* Header */}
      <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Tasks</h1>
          <p className="text-jee-muted">Manage your study tasks and track progress</p>
        </div>
        <Button
          onClick={() => setIsTaskModalOpen(true)}
          className="mt-4 lg:mt-0 bg-jee-primary text-white px-6 py-3 rounded-xl font-medium hover:bg-blue-700 transition-all duration-200 shadow-lg hover:shadow-xl transform hover:-translate-y-1"
          data-testid="button-add-task"
        >
          <Plus className="mr-2" size={18} />
          Add New Task
        </Button>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card className="p-4">
          <CardContent className="p-0">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-jee-muted">Total Tasks</p>
                <p className="text-2xl font-bold text-gray-900">{taskStats.total}</p>
              </div>
              <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
                <CheckCircle className="text-blue-600" size={20} />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="p-4">
          <CardContent className="p-0">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-jee-muted">Completed</p>
                <p className="text-2xl font-bold text-green-600">{taskStats.completed}</p>
              </div>
              <div className="w-10 h-10 bg-green-100 rounded-lg flex items-center justify-center">
                <CheckCircle className="text-green-600" size={20} />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="p-4">
          <CardContent className="p-0">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-jee-muted">Pending</p>
                <p className="text-2xl font-bold text-yellow-600">{taskStats.pending}</p>
              </div>
              <div className="w-10 h-10 bg-yellow-100 rounded-lg flex items-center justify-center">
                <Clock className="text-yellow-600" size={20} />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="p-4">
          <CardContent className="p-0">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-jee-muted">Overdue</p>
                <p className="text-2xl font-bold text-red-600">{taskStats.overdue}</p>
              </div>
              <div className="w-10 h-10 bg-red-100 rounded-lg flex items-center justify-center">
                <AlertCircle className="text-red-600" size={20} />
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <Card className="p-6">
        <div className="flex flex-col lg:flex-row gap-4">
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={18} />
              <Input
                placeholder="Search tasks..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
                data-testid="input-search-tasks"
              />
            </div>
          </div>

          <div className="flex gap-2">
            <select
              value={selectedSubject}
              onChange={(e) => setSelectedSubject(e.target.value as Subject | 'All')}
              className="px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-jee-primary"
              data-testid="select-filter-subject"
            >
              <option value="All">All Subjects</option>
              <option value="Physics">Physics</option>
              <option value="Chemistry">Chemistry</option>
              <option value="Mathematics">Mathematics</option>
            </select>

            <select
              value={selectedStatus}
              onChange={(e) => setSelectedStatus(e.target.value)}
              className="px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-jee-primary"
              data-testid="select-filter-status"
            >
              <option value="All">All Status</option>
              <option value="pending">Pending</option>
              <option value="in-progress">In Progress</option>
              <option value="completed">Completed</option>
              <option value="overdue">Overdue</option>
            </select>
          </div>
        </div>
      </Card>

      {/* Tasks List */}
      <div className="space-y-4">
        {filteredTasks.length === 0 ? (
          <Card className="p-12">
            <div className="text-center">
              <CheckCircle className="mx-auto mb-4 text-gray-400" size={48} />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No tasks found</h3>
              <p className="text-jee-muted mb-4">
                {tasks.length === 0 
                  ? "Create your first task to get started with your JEE preparation!"
                  : "Try adjusting your search or filter criteria."
                }
              </p>
              {tasks.length === 0 && (
                <Button
                  onClick={() => setIsTaskModalOpen(true)}
                  className="bg-jee-primary text-white"
                >
                  <Plus className="mr-2" size={16} />
                  Add Your First Task
                </Button>
              )}
            </div>
          </Card>
        ) : (
          filteredTasks.map((task) => {
            const status = getTaskStatus(task);
            const statusInfo = statusConfig[status];
            const StatusIcon = statusInfo.icon;

            return (
              <Card
                key={task.id}
                className="p-6 hover:shadow-md transition-all duration-200 animate-slide-up"
                data-testid={`task-card-${task.id}`}
              >
                <div className="flex items-start space-x-4">
                  <Checkbox
                    checked={task.status === 'completed'}
                    onCheckedChange={(checked) => handleTaskToggle(task.id, !!checked)}
                    className="mt-1"
                    data-testid={`checkbox-task-${task.id}`}
                  />

                  <div className="flex-1">
                    <div className="flex items-start justify-between mb-3">
                      <div>
                        <h3 
                          className={`text-lg font-semibold ${
                            task.status === 'completed' ? 'line-through text-gray-500' : 'text-gray-900'
                          }`}
                        >
                          {task.title}
                        </h3>
                        {task.description && (
                          <p className="text-sm text-jee-muted mt-1">{task.description}</p>
                        )}
                      </div>

                      <div className="flex items-center space-x-2">
                        <Badge className={statusInfo.className}>
                          <StatusIcon size={12} className="mr-1" />
                          {statusInfo.label}
                        </Badge>
                        <Badge className={subjectColors[task.subject]}>
                          {task.subject}
                        </Badge>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => handleDeleteTask(task.id)}
                          className="h-8 w-8 p-0 text-red-500 hover:text-red-700 hover:bg-red-50"
                          data-testid={`button-delete-task-${task.id}`}
                          title="Delete task"
                        >
                          <Trash2 size={14} />
                        </Button>
                      </div>
                    </div>

                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-4 text-sm text-jee-muted">
                        <span>Due: {formatDate(task.dueDate)}</span>
                        <span>Priority: {task.priority}</span>
                        {task.estimatedTime && (
                          <span>Est: {task.estimatedTime}min</span>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              </Card>
            );
          })
        )}
      </div>

      {/* Task Modal */}
      <SimpleTaskModal
        open={isTaskModalOpen}
        onClose={() => setIsTaskModalOpen(false)}
        onTaskCreated={handleTaskCreated}
      />
    </div>
  );
}

client/src/pages/timer.tsx.content:
import { useState, useEffect } from 'react';
import { Play, Pause, Square, RotateCcw, Settings, Clock } from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { useTimer } from '@/hooks/use-timer';
import { studySessionStorage, type UserProfile } from '@/lib/storage';
import type { Subject } from '@shared/schema';

const TIMER_PRESETS = [
  { label: 'Pomodoro', minutes: 25, description: 'Classic 25-minute focus session' },
  { label: 'Short Break', minutes: 5, description: 'Quick 5-minute break' },
  { label: 'Long Break', minutes: 15, description: 'Extended 15-minute break' },
  { label: 'Focus Block', minutes: 45, description: 'Deep focus 45-minute session' },
  { label: 'Study Hour', minutes: 60, description: 'Full hour study session' },
  { label: 'Quick Review', minutes: 10, description: '10-minute review session' },
];

interface TimerProps {
  userProfile: UserProfile | null;
}

export default function Timer({ userProfile }: TimerProps) {
  const [selectedSubject, setSelectedSubject] = useState<Subject>(
    userProfile?.preferences.defaultSubject || 'Physics'
  );
  const [selectedPreset, setSelectedPreset] = useState(
    TIMER_PRESETS.find(p => p.minutes === userProfile?.preferences.defaultTimerDuration) || TIMER_PRESETS[0]
  );
  const [sessionStartTime, setSessionStartTime] = useState<string | null>(null);
  const [sessions, setSessions] = useState(studySessionStorage.getAll());
  const [customMinutes, setCustomMinutes] = useState(25);

  const {
    timeLeft,
    formattedTime,
    isRunning,
    isPaused,
    progress,
    start,
    pause,
    reset,
    setTime,
  } = useTimer(selectedPreset.minutes * 60);

  useEffect(() => {
    setTime(selectedPreset.minutes * 60);
  }, [selectedPreset, setTime]);

  const handleStart = () => {
    if (!isRunning && !isPaused) {
      setSessionStartTime(new Date().toISOString());
    }
    start();
  };

  const handlePause = () => {
    pause();
  };

  const handleStop = () => {
    if (sessionStartTime) {
      const endTime = new Date().toISOString();
      const startTime = new Date(sessionStartTime);
      const endTimeDate = new Date(endTime);
      const duration = Math.round((endTimeDate.getTime() - startTime.getTime()) / 1000 / 60);

      if (duration > 0) {
        studySessionStorage.create({
          subject: selectedSubject,
          duration,
          startTime: sessionStartTime,
          endTime,
          notes: `${duration} minute ${selectedPreset.label} session`,
        });
        setSessions(studySessionStorage.getAll());
      }
    }

    reset();
    setSessionStartTime(null);
  };

  const handleReset = () => {
    reset();
    setSessionStartTime(null);
  };

  const handlePresetClick = (preset: typeof TIMER_PRESETS[0]) => {
    if (!isRunning) {
      setSelectedPreset(preset);
    }
  };

  const handleCustomTime = () => {
    if (!isRunning && customMinutes > 0) {
      setTime(customMinutes * 60);
    }
  };

  // Calculate stroke dash offset for circular progress
  const radius = 120;
  const circumference = 2 * Math.PI * radius;
  const strokeDashoffset = circumference - (progress * circumference);

  const todaysSessions = sessions.filter(session => {
    const sessionDate = new Date(session.startTime).toDateString();
    const today = new Date().toDateString();
    return sessionDate === today;
  });

  const todaysStudyTime = todaysSessions.reduce((acc, session) => acc + session.duration, 0);

  return (
    <div className="max-w-4xl mx-auto space-y-6 animate-fade-in">
      {/* Header */}
      <div className="text-center">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Study Timer</h1>
        <p className="text-jee-muted">Focus on your studies with structured time management</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Timer Section */}
        <div className="lg:col-span-2">
          <Card className="p-8">
            <CardContent className="p-0">
              {/* Subject Selection */}
              <div className="text-center mb-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-3">Study Subject</h3>
                <div className="flex justify-center gap-2">
                  {(['Physics', 'Chemistry', 'Mathematics'] as Subject[]).map((subject) => (
                    <Button
                      key={subject}
                      onClick={() => setSelectedSubject(subject)}
                      disabled={isRunning}
                      variant={selectedSubject === subject ? 'default' : 'outline'}
                      className={selectedSubject === subject ? 'jee-primary' : ''}
                      data-testid={`button-subject-${subject.toLowerCase()}`}
                    >
                      {subject}
                    </Button>
                  ))}
                </div>
              </div>

              {/* Timer Display */}
              <div className="text-center mb-8">
                <div className="relative w-80 h-80 mx-auto mb-6">
                  <svg className="w-80 h-80 transform -rotate-90" viewBox="0 0 280 280">
                    <circle
                      cx="140"
                      cy="140"
                      r="120"
                      stroke="#E5E7EB"
                      strokeWidth="12"
                      fill="none"
                    />
                    <circle
                      cx="140"
                      cy="140"
                      r="120"
                      stroke="var(--jee-primary)"
                      strokeWidth="12"
                      fill="none"
                      strokeDasharray={circumference}
                      strokeDashoffset={strokeDashoffset}
                      strokeLinecap="round"
                      className="transition-all duration-300"
                      data-testid="timer-progress-circle"
                    />
                  </svg>
                  <div className="absolute inset-0 flex items-center justify-center">
                    <div className="text-center">
                      <div 
                        className="text-5xl font-bold text-gray-900 mb-2"
                        data-testid="timer-display"
                      >
                        {formattedTime}
                      </div>
                      <div className="text-lg text-jee-muted">{selectedPreset.label}</div>
                      <div className="text-sm text-jee-muted">{selectedSubject}</div>
                    </div>
                  </div>
                </div>

                {/* Timer Controls */}
                <div className="flex justify-center space-x-4">
                  <Button
                    onClick={handleStart}
                    disabled={isRunning}
                    size="lg"
                    className="w-16 h-16 rounded-full jee-primary hover:bg-blue-700 transition-all transform hover:scale-105"
                    data-testid="button-timer-start"
                  >
