          ) : (
            schedule.map((item) => {
              const colorClass = subjectColors[item.subject];

              return (
                <div
                  key={item.id}
                  className={`flex items-center space-x-3 p-3 rounded-lg transition-all duration-200 ${
                    item.completed 
                      ? 'bg-gray-100 opacity-60' 
