import { X } from 'lucide-react';
import { Button } from '@/components/ui/button';

interface SimpleModalProps {
  open: boolean;
  onClose: () => void;
  title: string;
  children: React.ReactNode;
  className?: string;
  size?: 'sm' | 'md' | 'lg' | 'xl';
  scrollable?: boolean;
}

export function SimpleModal({ 
  open, 
  onClose, 
  title, 
  children, 
  className = '', 
  size = 'md',
  scrollable = true 
}: SimpleModalProps) {
  if (!open) return null;

  const sizeClasses = {
    sm: 'max-w-sm',
    md: 'max-w-sm sm:max-w-md',
    lg: 'max-w-sm sm:max-w-md lg:max-w-lg',
    xl: 'max-w-sm sm:max-w-md lg:max-w-lg xl:max-w-xl'
  };

  const modalSizeClass = `modal-${size}`;

  return (
    <div 
      className="fixed inset-0 z-[99999] flex items-center justify-center p-4 modal-backdrop overflow-auto"
      style={{ 
        backgroundColor: 'rgba(0, 0, 0, 0.5)',
        zIndex: 99999
      }}
      onClick={(e) => {
        if (e.target === e.currentTarget) onClose();
      }}
    >
      <div 
        className={`modal-content ${scrollable ? 'modal-content-scrollable' : ''} ${modalSizeClass} bg-white dark:bg-gray-800 rounded-lg shadow-xl w-full ${sizeClasses[size]} max-h-[90vh] overflow-hidden ${className}`}
        style={{ 
          zIndex: 100000,
          margin: 'auto'
        }}
        onClick={(e) => e.stopPropagation()}
      >
        {/* Header - Fixed */}
